        setupSearch();
        displayCategories();
        displayLinks(config.links);
      };

      const renderPortfolioSection = () => {
        ui.portfolioSection.innerHTML = `<div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"></div>`;
        const grid = ui.portfolioSection.firstChild;
        config.portfolio.forEach((item, index) => {
            const card = document.createElement('div');
            card.className = 'animate-card bg-white border border-[color:var(--ui-border-base)] rounded-lg shadow-sm overflow-hidden transition-all duration-200 hover:shadow-lg hover:-translate-y-0.5';
            card.style.setProperty('--animation-delay', `${index * 100}ms`);
            card.innerHTML = `<img src="${item.image}" alt="${item.title}" class="w-full h-48 object-cover"><div class="p-5"><h3 class="text-lg font-semibold text-[color:var(--ui-fg-base)]">${item.title}</h3><p class="text-sm text-[color:var(--ui-fg-muted)] mt-1">${item.description}</p><div class="mt-4 flex space-x-4"><a href="${item.liveUrl}" target="_blank" class="text-sm font-medium text-[color:var(--ui-fg-interactive)] hover:underline">Live Demo <i class="bi bi-arrow-up-right ml-1"></i></a><a href="${item.detailsUrl}" target="_blank" class="text-sm font-medium text-[color:var(--ui-fg-interactive)] hover:underline">Details</a></div></div>`;
