        document.getElementById('contactForm').addEventListener('submit', handleContactSubmit);
      };

      const handleContactSubmit = async (event) => {
        event.preventDefault();
        const form = event.target;
        const submitBtn = form.querySelector('#submitBtn');
        const formStatus = form.querySelector('#formStatus');
        const { name, email, message } = form.elements;
        submitBtn.disabled = true;
        submitBtn.textContent = 'Sending...';
        formStatus.textContent = '';

        // Telegram Bot Message Configuration

        const text = `New Message from Portfolio\n\n*Name:* ${name.value}\n*Email:* ${email.value}\n\n*Message:*\n${message.value}`;
        const url = `https://api.telegram.org/bot${config.telegram.botToken}/sendMessage`;
        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ chat_id: config.telegram.chatId, text, parse_mode: 'Markdown' }),
            });
            const data = await response.json();
            if (data.ok) {
                formStatus.textContent = 'Message sent successfully!';
                formStatus.style.color = 'green';
                form.reset();
            } else { throw new Error(data.description); }
        } catch (error) {
            formStatus.textContent = 'Failed to send message. Please try again later.';
            formStatus.style.color = 'red';
        } finally {
            submitBtn.disabled = false;
            submitBtn.textContent = 'Send Message';
        }
      };

      const displayLinks = (links) => {
        const linksGrid = document.getElementById('linksGrid');
        const noResults = document.getElementById('noResults');
        linksGrid.innerHTML = '';
        noResults.classList.toggle('hidden', links.length > 0);
        links.forEach((link, index) => {
            const key = link.platform.toLowerCase().replace(/[^a-z0-9]/g, '');
            const iconName = config.iconMap[key] || config.iconMap['default'];
            const linkElement = document.createElement('a');
            linkElement.href = link.url;
            linkElement.target = '_blank';
            linkElement.rel = 'noopener noreferrer';
            linkElement.className = 'group animate-card flex items-center justify-between p-4 bg-white border border-[color:var(--ui-border-base)] rounded-lg shadow-sm hover:shadow-md hover:-translate-y-0.5 active:scale-[0.98] transition-all duration-200 ease-in-out';
            linkElement.style.setProperty('--animation-delay', `${index * 30}ms`);
            const highlightedName = state.searchQuery ? link.platform.replace(new RegExp(`(${state.searchQuery.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&')})`, 'gi'), '<span class="search-highlight">$1</span>') : link.platform;
            linkElement.innerHTML = `<div class="flex items-center"><i class="bi ${iconName} text-xl text-[color:var(--ui-fg-muted)] transition-colors group-hover:text-[color:var(--ui-fg-interactive)]"></i><span class="ml-4 font-medium text-[color:var(--ui-fg-subtle)] transition-colors group-hover:text-[color:var(--ui-fg-base)]">${highlightedName}</span></div><i class="bi bi-arrow-up-right text-lg text-gray-400 group-hover:text-[color:var(--ui-fg-interactive)] transition-colors"></i>`;
            linksGrid.appendChild(linkElement);
        });
      };

      const displayCategories = () => {
        const categoryFilter = document.getElementById('categoryFilter');
        const categories = ['all', ...new Set(config.links.map(link => link.category))];
        categoryFilter.innerHTML = '';
        categories.forEach(category => {
            const button = document.createElement('button');
            button.className = `px-3 py-1 rounded-full text-sm font-medium transition-colors ${state.activeCategory === category ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-700 hover:bg-gray-200'}`;
            button.textContent = category === 'all' ? 'All' : config.categoryNames[category] || category;
            button.addEventListener('click', () => {
                state.activeCategory = category;
                displayCategories();
                displayLinks(filterLinks());
            });
            categoryFilter.appendChild(button);
        });
      };

      const filterLinks = () => config.links.filter(link => (state.activeCategory === 'all' || link.category === state.activeCategory) && (!state.searchQuery || link.platform.toLowerCase().includes(state.searchQuery.toLowerCase())));

      const setupSearch = () => {
        const searchInput = document.getElementById('searchInput');
        const searchClear = document.getElementById('searchClear');
        searchInput.addEventListener('input', (e) => {
            state.searchQuery = e.target.value.trim();
            searchClear.classList.toggle('hidden', !state.searchQuery);
            displayLinks(filterLinks());
        });
        searchClear.addEventListener('click', () => {
            searchInput.value = '';
            state.searchQuery = '';
            searchClear.classList.add('hidden');
            displayLinks(filterLinks());
        });
      };

      const setupNavigation = () => {
        config.navigation.forEach(item => {
          const link = document.createElement('button');
          link.className = 'nav-link';
          link.dataset.target = item.id;
          link.innerHTML = `<i class="bi ${item.icon}"></i><span class="pb-[3px]">${item.name}</span>`;
          ui.navContainer.appendChild(link);
        });

        document.querySelectorAll('.nav-link').forEach(link => {
          link.addEventListener('click', () => showSection(link.dataset.target));
        });
      };

      const showSection = (targetId, isInitialLoad = false) => {
          state.activeSection = targetId;
          ui.contentSections.forEach(section => section.classList.toggle('hidden', section.id !== targetId));

          document.querySelectorAll('.nav-link').forEach(link => {
              const isActive = link.dataset.target === targetId;
              const navItemData = config.navigation.find(nav => nav.id === link.dataset.target);
              link.classList.toggle('active', isActive);
              link.querySelector('i').className = `bi ${isActive ? navItemData.activeIcon : navItemData.icon}`;
          });

          if (!isInitialLoad) {
            window.scrollTo({ top: 0, behavior: 'smooth' });
          }
      };

      const handleRedirect = () => {
        const slug = window.location.pathname.split('/').filter(Boolean)[0];
        if (!slug) return false;
        const redirectLink = config.links.find(link => link.platform.toLowerCase().replace(/[^a-z0-9]/g, '') === slug.toLowerCase().replace(/[^a-z0-9]/g, ''));
        if (redirectLink) {
            ui.loader.style.display = 'flex'; ui.loader.style.opacity = '1';
            ui.loader.querySelector('p').textContent = `Redirecting to ${new URL(redirectLink.url).hostname}...`;
            setTimeout(() => { window.location.replace(redirectLink.url); }, 100);
            return true;
        }
        return false;
      };

      const updateSchema = (links) => {
        const schemaScript = document.getElementById('schema-data');
        const schema = JSON.parse(schemaScript.textContent);
        schema.sameAs = links.map(link => link.url);
        schemaScript.textContent = JSON.stringify(schema, null, 2);
      };

      init();
    });
